import Vue from 'vue'
import Router from 'vue-router'
const Home = () => import('@/components/home/home')
const LoginORregister = () => import('@/components/loginorregister/loginorregister')
const LoginPanel = () => import('@/components/base/logregpwdpanel/loginpanel/loginpanel')
const RegisterPanel = () => import('@/components/base/logregpwdpanel/registerpanel/registerpanel')
const SendEmailPanel = () => import('@/components/base/logregpwdpanel/sendemailpanel/sendemailpanel')
const SendVCodePanel = () => import('@/components/base/logregpwdpanel/sendvcodepanel/sendvcodepanel')
const NewPwdPanel = () => import('@/components/base/logregpwdpanel/newpwdpanel/newpwdpanel')
const Recommends = () => import('@/components/base/recommends/recommends')
const Article = () => import('@/components/article/article')
const User = () => import('@/components/user/user')
const Profile = () => import('@/components/profile/profile')
const ChangeImg = () => import('@/components/changeuserimg/changeuserimg')
Vue.use(Router)

export const createRouter = () => {
  return new Router({
    mode: 'history',
    base: '/',
    routes: [
      {
        path: '/',
        name: 'Home',
        component: Home,
        redirect: '/homepage',
        children: [
          {
            path: 'homepage',
            component: Recommends
          },
          {
            path: 'article/:id',
            name: 'Article',
            component: Article
          },
        ]
      },
      {
        path: '/user',
        name: 'user',
        component: User,
        meta: { requiresAuth: true },
        redirect: '/user/profile',
        children: [
          { path: 'profile', name: 'profilepanel', component: Profile, meta: { requiresAuth: true } },
          { path: 'changeimg', name: 'changeimg', component: ChangeImg, meta: { requiresAuth: true } }
        ]
      },
      {
        path: '/loginORregister',
        component: LoginORregister,
        name: 'LoginORregister',
        redirect: '/loginORregister/login',
        children: [
          { path: 'login', name: 'loginpanel', component: LoginPanel },
          { path: 'register', name: 'registerpanel', component: RegisterPanel },
          { path: 'sendemail', name: 'sendemailpanel', component: SendEmailPanel },
          { path: 'sendvcode', name: 'sendvcodepanel', component: SendVCodePanel },
          { path: 'newpwd', name: 'newpwdpanel', component: NewPwdPanel },
        ]
      }
    ],
    scrollBehavior(to, from, savedPosition) {
      return { x: 0, y: 0 }
    }
  })
}

/**
 * 获取的数据需要位于视图组件之外，即放置在专门的数据预取存储容器(data store)或"状态容器(state container)）"中。
 * 首先，在服务器端，我们可以在渲染之前预取数据，并将数据填充到 store 中。此外，我们将在 HTML 中序列化(serialize)
 * 和内联预置(inline)状态
 */

import Vue from 'vue'
import Vuex from 'vuex'
import Promise from 'promise'
Vue.use(Vuex)

import {
  getRecommends,
  getRecommend,
  getRecommendByTypes,
  getRecommendTypesCount,
  getRecommendsCount
} from './src/api/recommend/recommend'

export const createStore = () => {
  return new Vuex.Store({
    state: {
      recommends: [],
      recommend: [],
      count: 0,
      typesCount: 0,
      typeRecommend: [],
      category: []
    },
    actions: {
      getHomepageData(store, page) {
        return Promise.all([
          store.dispatch('getRecommends', page),
          store.dispatch('getRecommendTypesCount'),
          store.dispatch('getRecommendsCount')
        ])
      },
      getRecommends({ commit }, page) {
        return getRecommends(page).then(payload => {
          commit('setRecommends', payload.data)
        })
      },
      getRecommend({ commit }, id) {
        return getRecommend(id).then(payload => {
          commit('setRecommend', payload.data)
        })
      },
      getRecommendByTypes({ commit }, type, page) {
        return getRecommendByTypes(type, page).then(payload => {
          commit('setRecommends', payload.data)
        })
      },
      getRecommendTypesCount({ commit }) {
        return getRecommendTypesCount().then(payload => {
          commit('setCategory', payload.data)
        })
      },
      getRecommendsCount({ commit }) {
        return getRecommendsCount().then(payload => {
          commit('setRecommendsCount', payload.data)
        })
      }
    },
    mutations: {
      setRecommends(state, payload) {
        state.recommends = payload
      },
      appendRecommends(state, payload) {
        state.recommends.push(...payload)
      },
      resetRecommends(state) {
        state.recommends = []
      },
      setRecommend(state, payload) {
        state.recommend = payload
      },
      setCategory(state, payload) {
        state.category = payload
      },
      setRecommendsCount(state, payload) {
        state.count = payload
      },
    }
  })
}

