import 'babel-polyfill';
import Vue from 'vue'
import App from './App.vue'
import { createRouter } from './router'
import { createStore } from './store'
import { sync } from 'vuex-router-sync'
import Snotify from 'vue-snotify';
import 'font-awesome/css/font-awesome.css';
// import { Select, DatePicker, Option } from 'element-ui';
const Select = () => import('element-ui/lib/select')
const DatePicker = () => import('element-ui/lib/date-picker')
const Option = () => import('element-ui/lib/option')
import 'element-ui/lib/theme-chalk/index.css';
Vue.use(Select);
Vue.use(DatePicker);
Vue.use(Option);
Vue.use(Snotify);
// 导出一个工厂函数，用于创建新的
// 应用程序、router 和 store 实例

export const createApp = ssrContext => {
  const router = createRouter()
  const store = createStore()

  // 同步路由状态(route state)到 store
  sync(store, router)

  const app = new Vue({
    router,
    store,
    ssrContext,
    render: h => h(App)
  })
  return { 
    app, 
    store, 
    router 
  }
}

